import jwt_decode from "jwt-decode";
import Error from './pages/error';
import Home from './pages/homes';
import { useEffect, useState } from 'react';
import error from "./pages/error";


function App() {
  const [screen, setScreen] = useState('Initial');
  const [name, setName] = useState('');
  const queryParameters = new URLSearchParams(window.location.search);
  const token = queryParameters.get('token');
  const [status, setStatus] = React.useState('');
  const [mapLink, setMapLink] = React.useState({ href: '', textContent: '' });

  useEffect(() => {
    try {
      const decoded = jwt_decode(token);
      console.log(decoded);
      setName(decoded.name);
      const expiryTime = decoded.exp;
      const currentTime = Math.floor(Date.now() / 1000);
      if (currentTime < expiryTime) {
        checkPermission();
      } else {
        setScreen('Expired');
      }
    } catch (error) {
      setScreen('Invalid');
    }
  }, [token]);

  const success = React.useCallback((position) => {
    const latitude = position.coords.latitude;
    const longitude = position.coords.longitude;

    setStatus('');
    setMapLink({
      href: `https://www.openstreetmap.org/#map=18/${latitude}/${longitude}`,
      textContent: `Latitude: ${latitude} °, Longitude: ${longitude} °`
    });
  }, []);

  const error = React.useCallback(() => {
    setStatus('Unable to retrieve your location');
  }, []);

  const geoFindMe = React.useCallback(() => {
    setMapLink({ href: '', textContent: '' });

    if (!navigator.geolocation) {
      setStatus('Geolocation is not supported by your browser');
    } else {
      setStatus('Locating…');
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }, [success, error]);

  const checkPermission = ()=>{
    if('geolocation' in navigator){
      navigator.permissions.query({name: 'geolocation'}).then(result => {
         if(result.state === "granted"){
           setScreen('Home');
         } else if( result.state === "prompt"){
           permissionBox(result);
         } else{
          permissionDenied();
         }
      }).catch(error => {
        console.error('Error checking location permission:', error);
      });
    }
    else{
setScreen('No-GPS')
    }
  }

  const permissionBox = result => {
    if (window.confirm('Allow this site to access your location?')) {
      navigator.geolocation.getCurrentPosition(
        () => {
          setScreen('Home')
        },
        () => {
          permissionDenied();
        }
      );
    } else {
      permissionDenied();
    }
  };

  const permissionDenied = () => {
    alert('Location permission denied. Please enable it in your browser settings.');
  };

  // const checkLocationStatus = () => {
  //   navigator.permissions.query({ name: "geolocation" }).then((result) => {

  //     result.addEventListener("change", () => {
  //       console.log(result.state);
  //       if (result.state === "granted") {


  //         setScreen('Home');
  //       } else if (result.state === "denied") {
  //         navigator.permissions.request({ name: "geolocation" }).then((permissionStatus) => {
  //           if (permissionStatus.state === "granted") {
  //             setScreen('Home');
  //           }
  //         });
  //         setScreen('No-GPS');


  //       }
  //     })
  //   })
  //   navigator.geolocation.getCurrentPosition(
  //     () => {
  //       setScreen('Home');

  //     },
  //     () => {
  //       setScreen('No-GPS');
  //     }
  //   );

  // };

  switch (screen) {
    case 'Initial':
      return (
        <div>
          <Error image={'expired.svg'} title={'Please Wait'} subTitle={'Loading'} />
        </div>
      );
    case 'Home':
      return (
        <div>
          <Home name={name} />
        </div>
      );
    case 'Expired':
      return (
        <div>
          <Error
            image={'expired.svg'}
            title={'Oops, this link is expired!'}
            subTitle={'Please request a new link from an administrator'}
          />
        </div>
      );
    case 'Invalid':
      return (
        <div>
          <Error image={'invalid.svg'} title={'Test'} subTitle={'Test SubTitle'} />
        </div>
      );
    case 'No-GPS':
      return (
        <div>
          <Error image={'no_gps.svg'} title={'No GPS!'} subTitle={"Satellite can't find you"} />
        </div>
      );
    default:
      return (
        <div>
          <Error image={'no_gps.svg'} title={'No GPS!'} subTitle={"Satellite can't find you"} />
        </div>
      );
  }
}
export default App;
