import React, { useState } from "react";
import "../style.css";
import Camera from "react-html5-camera-photo";

export default function home({ token, name }) {
  const [status, setStatus] = useState('');
  const [mapLink, setMapLink] = useState({ href: '', textContent: '' });

  const success = React.useCallback((position) => {
    const latitude = position.coords.latitude;
    const longitude = position.coords.longitude;

    setStatus('');
    setMapLink({
      href: `https://www.openstreetmap.org/#map=18/${latitude}/${longitude}`,
      textContent: `Latitude: ${latitude} °, Longitude: ${longitude} °`
    });
  }, []);

  const error = React.useCallback(() => {
    setStatus('Unable to retrieve your location');
  }, []);

  const getLocation = React.useCallback(() => {
    setMapLink({ href: '', textContent: '' });

    if (!navigator.geolocation) {
      setStatus('Geolocation is not supported by your browser');
    } else {
      setStatus('Locating…');
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }, [success, error]);


  return (
    <>
      <div className="main">
        <div>
          <div className="heading">
            <h2>Welcome</h2>
            <h2>{name}</h2>
          </div>

          {/* <button className="primary-color" onClick={takePhoto}>Take Photo
          <Camera 
          onTakePhoto={(uri) => { takePhoto(uri)}}/>
          </button> */}
          <button className="primary-color" onClick={getLocation}>Current Location</button>
          <p id="status">{status}</p>
          <a id="map-link" href={mapLink.href}>{mapLink.textContent}</a>
        </div>
      </div>
    </>
  );
}
