import React, { useState } from "react";
import "../style.css";
//import Webcam from 'react-webcam';
// import Camera from "react-html5-camera-photo";

export default function Home({ token, name }) {
  const [status, setStatus] = React.useState("");
  const [mapLink, setMapLink] = React.useState({ href: "", textContent: "" });

  const success = React.useCallback((position) => {
    const latitude = position.coords.latitude;
    const longitude = position.coords.longitude;

    setStatus("");
    setMapLink({
      href: `https://www.openstreetmap.org/#map=18/${latitude}/${longitude}`,
      textContent: `Latitude: ${latitude} °, Longitude: ${longitude} °`,
    });
  }, []);

  const error = React.useCallback(() => {
    setStatus("Unable to retrieve your location");
  }, []);

  const getLocation = React.useCallback(() => {
    setMapLink({ href: "", textContent: "" });

    if (!navigator.geolocation) {
      setStatus("Geolocation is not supported by your browser");
    } else {
      setStatus("Locating…");
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }, [success, error]);

  // CAMERA

  const [imgData, setImgData] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);

  const startCamera = () => {
    setCameraActive(true);
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices
        .getUserMedia({ video: true })
        .then(function (stream) {
          let video = document.getElementById("videoPreview");
          video.srcObject = stream;
          video.play();
        })
        .catch((error) => {
          console.log("Enable to access camera: ", error);
        });
    }
  };

  const capturePhoto = () => {
    let video = document.getElementById("videoPreview");
    let canvas = document.getElementById("photoCanvas");
    let context = canvas.getContext("2d");
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    let dataURL = canvas.toDataURL("image/png");
    setImgData(dataURL);
    setCameraActive(false);
    video.srcObject.getTracks()[0].stop();
  };

  // const [cameraActive, setCameraActive] = React.useState(true);
  // const videoConstraints = {
  //   width: 380,
  //   height: 720,
  //   facingMode: "user",
  // };
  // const webcamRef = React.useRef(null);

  // const captureImage = React.useCallback(() => {

  //   const imageSrc = webcamRef.current.getScreenshot();
  //   localStorage.setItem('capturedImage', imageSrc);
  //   setCameraActive(false);
  // }, [webcamRef]);

  // const savedImage = localStorage.getItem('capturedImage');
  return (
    <>
      <div className="main">
        <div>
          <div className="heading">
            <h2>Welcome</h2>
            <h2>{name}</h2>
          </div>
          {!cameraActive && (
            <div>
              <button onClick={startCamera}>Start Camera</button>
            </div>
          )}

          {cameraActive && (
            <div>
              <video id="videoPreview" width="100%" autoPlay></video>
              <button onClick={capturePhoto}>Capture Photo</button>
            </div>
          )}

          {/* {cameraActive ? (
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          videoConstraints={videoConstraints}
        />
      ) : (
        <h2>{savedImage}</h2>
      )} <br /> */}

         

          <button className="primary-color" onClick={getLocation}>
            Current Location
          </button>
          <p id="status">{status}</p>
          <a id="map-link" href={mapLink.href}>
            {mapLink.textContent}
          </a>
        </div>
      </div>
    </>
  );
}
