import './App.css';
import jwt_decode from "jwt-decode";
import Error from './pages/error';
import { useState } from 'react';


function App() {
  const [screen, setScreen] = useState({});
  setScreen('Initial');
  const queryParameters = new URLSearchParams(window.location.search);
  const token = queryParameters.get('token');

  try{
    const decoded = jwt_decode(token);
    const name = decoded.name;
    const expiryTime = decoded.exp;
    const currentTime = Math.floor(Date.now() / 1000);

    if(currentTime < expiryTime){
      setScreen('No-GPS')
    }else{
      setScreen('Expired')
    }

  }catch(error){
    setScreen('Invalid')
  }

if(screen === 'Initial'){
  return (
    <>
    <div>
    <Error image={"expired.svg"}
      title={"Please Wait"}
      subTitle={"Loading"} />
    </div>
    </>
  );
}else if(screen === 'Expired'){
  return (
    <>
    <div>
    <Error image={"expired.svg"}
      title={"Oops, this link is expired!"}
      subTitle={"Please request a new link from an administrator"} />
    </div>
    </>
  );
}else if (screen === 'Invalid'){
  return (
    <>
    <div>
    <Error image={"invalid.svg"}
      title={"Test"}
      subTitle={"Test SubTUtle"} />
    </div>
    </>
  );
}else if (screen === 'No-GPS'){
  return (
    <>
    <div>
    <Error image={"no_gps.svg"}
      title={"No GPS!"}
      subTitle={"Satelite can't find you"} />
    </div>
    </>
  );
}  else{
  return (
    <>
    <div>
    <Error image={"no_gps.svg"}
      title={"No GPS!"}
      subTitle={"Satelite can't find you"} />
    </div>
    </>
  );
}

}

export default App;
